3. Code
Write the MinStack() class and its requried methods.

class MinStack():
    def __init__(self):
        self.stack = [] # create empty list

    def push(self, val: int) -&gt; None:
        self.stack.append(val) # add value to end of list

    def pop(self) -&gt; None:
        self.stack.pop() # remove last val of the list

    def top(self) -&gt; int:
        return self.stack[-1] # in a list, top of stack is the last item list[-1]

    def getMin(self) -&gt; int:
        return min(self.stack)

6. Clean Version

class Solution:
    def evalRPN_clean(self, tokens: [str]) -&gt; int:
        stack = []
        stack.append(int(tokens[0])) 
        operators = ['*', '+', '-', '/'] 

        def operate(operator,x2,x1):
            if operator == '+':
                return x2+x1
            elif operator == '-':
                return x2-x1
            elif operator == '*':
                return x2*x1
            else:
                return int(x2/x1)

        def pop_top_2(stack):
            top_1 = stack.pop()
            top_2 = stack.pop()
            return [top_2,top_1]

        for i in range(1,len(tokens)):
            curr_chr = tokens[i]
            if curr_chr in operators:
                top_2_items = pop_top_2(stack) 
                res   = operate(curr_chr, top_2_items[0],top_2_items[1])
                stack.append(res)
            else: 
                stack.append(int(curr_chr))
        return stack[0] ############### fixed after submission 2  ###############
soln = Solution()

